package com.syntax.class10;

public class HW9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=3, tot=0;

		do{

		   tot=tot+k;

		   k++;

		} while(k<11);

		System.out.print(tot);
		
		int z=2, sum=0;

		while(z<9) {

		   z++;

		   sum=sum+z;

		}

		System.out.println(sum);
		int m=2, total=0;

		while(m<6) {

		   total=total+m;

		   m++;

		}

		System.out.print(total);

	}

}
